// components/AddBookPopup.js
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addBook } from '../redux/actions';
import '../index.css'; // Import the CSS file

const AddBookPopup = ({ onClose }) => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState(''); // Add description state

  const dispatch = useDispatch();

  const handleSubmit = () => {
    dispatch(addBook({ name, price, category, description })); // Pass description to addBook action
    setName('');
    setPrice('');
    setCategory('');
    setDescription(''); // Clear description after submission
    onClose();
  };

  const handleCancel = () => {
    onClose();
  };

  return (
    <div className="overlay">
      <div className="popup">
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add Book</h2>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
          className="input"
          required
        />
        <input
          type="text"
          placeholder="Price"
          value={price}
          onChange={e => setPrice(e.target.value)}
          className="input"
          required
        />
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={e => setCategory(e.target.value)}
          className="input"
          required
        />
        <textarea // Add textarea for description input
          placeholder="Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
          className="textarea"
        />
        <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
          <button onClick={handleSubmit} className="button">Add Book</button>
          <button onClick={handleCancel} className="cancelButton">Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default AddBookPopup;
