import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { deleteBook } from '../redux/actions';

const BookList = () => {
  const books = useSelector(state => state.books);
  const dispatch = useDispatch();

  const handleDelete = id => {
    dispatch(deleteBook(id));
  };

  return (
    <div className="bookListContainer"> {/* Added a div around the BookList content */}
      {books.map(book => (
        <div key={book.id} className="bookItem"> {/* Wrapped each book item in a div */}
          <h3>{book.name}</h3>
          <p>Price: ${book.price}</p>
          <p>Category: {book.category}</p>
          <button onClick={() => handleDelete(book.id)} className="deleteButton">Delete</button>
        </div>
      ))}
    </div>
  );
};

export default BookList;
