import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addBook } from '../redux/actions';
import './AddBookPopup.css'; // Import the CSS file

const AddBookPopup = ({ onClose }) => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');

  const dispatch = useDispatch();

  const handleSubmit = () => {
    dispatch(addBook({ name, price, category }));
    setName('');
    setPrice('');
    setCategory('');
    onClose(); // Close the popup
  };

  return (
    <div className="overlay">
      <div className="popup">
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add Book</h2>
        <input 
          type="text" 
          placeholder="Name" 
          value={name} 
          onChange={e => setName(e.target.value)} 
          className="input" 
        />
        <input 
          type="text" 
          placeholder="Price" 
          value={price} 
          onChange={e => setPrice(e.target.value)} 
          className="input" 
        />
        <input 
          type="text" 
          placeholder="Category" 
          value={category} 
          onChange={e => setCategory(e.target.value)} 
          className="input" 
        />
        <button onClick={handleSubmit} className="button">Add Book</button>
      </div>
    </div>
  );
};

export default AddBookPopup;
